mapper.update:
update orders set end_time = #{param2} where orders_id = #{param1}
and unix_timestamp(end_time) < unix_timestamp(#{param2})
and (not exists (select start_time from orders where room_number = (select room_number from orders
        where orders_id = #{param1}) and start_time < #{param2}
        and start_time > (select end_time from orders where orders_id = #{param1}))
)
